import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class LongestDecreasingSubsequence {

    /**
     * Finds the longest decreasing subsequence in an array of integers.
     *
     * @param nums The input array of integers.
     * @return A list representing the longest decreasing subsequence.
     *         If multiple longest decreasing subsequences exist, returns one of them.
     */
    public static List<Integer> longestDecreasingSubsequence(int[] nums) {
        // Handle edge cases: null or empty array
        if (nums == null || nums.length == 0) {
            return new ArrayList<>(); // Return an empty list
        }

        int n = nums.length;
        List<Integer> longestSubsequence = new ArrayList<>();

        // Iterate through each element as a potential starting point
        for (int i = 0; i < n; i++) {
            List<Integer> currentSubsequence = new ArrayList<>();
            currentSubsequence.add(nums[i]); // Start the subsequence with the current element

            // Build the decreasing subsequence starting from the next element
            for (int j = i + 1; j < n; j++) {
                if (nums[j] < currentSubsequence.get(currentSubsequence.size() - 1)) {
                    currentSubsequence.add(nums[j]);
                }
            }

            // Update the longest subsequence if the current one is longer
            if (currentSubsequence.size() > longestSubsequence.size()) {
                longestSubsequence = new ArrayList<>(currentSubsequence); // Create a copy to avoid modification issues
            }
        }

        // If no decreasing subsequences were found (e.g., increasing array), return the single element array with the first element.
        if (longestSubsequence.isEmpty()) {
            longestSubsequence.add(nums[0]);
        }

        return longestSubsequence;
    }

    /**
     * Main method for testing the longestDecreasingSubsequence function.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // Test cases
        int[] arr1 = {5, 2, 9, 3, 7, 1, 8, 6};
        System.out.println("Input: " + Arrays.toString(arr1) + ", Output: " + longestDecreasingSubsequence(arr1)); // Expected: [9, 3, 1] or similar

        int[] arr2 = {10, 8, 5, 3, 1};
        System.out.println("Input: " + Arrays.toString(arr2) + ", Output: " + longestDecreasingSubsequence(arr2)); // Expected: [10, 8, 5, 3, 1]

        int[] arr3 = {1, 2, 3, 4, 5};
        System.out.println("Input: " + Arrays.toString(arr3) + ", Output: " + longestDecreasingSubsequence(arr3)); // Expected: [1]

        int[] arr4 = {5, 4, 3, 2, 1};
        System.out.println("Input: " + Arrays.toString(arr4) + ", Output: " + longestDecreasingSubsequence(arr4)); // Expected: [5, 4, 3, 2, 1]

        int[] arr5 = {5, 5, 5, 5, 5};
        System.out.println("Input: " + Arrays.toString(arr5) + ", Output: " + longestDecreasingSubsequence(arr5)); // Expected: [5]

        int[] arr6 = {7, 2, 1, 0, -5};
        System.out.println("Input: " + Arrays.toString(arr6) + ", Output: " + longestDecreasingSubsequence(arr6)); // Expected: [7, 2, 1, 0, -5]

        int[] arr7 = {}; // Empty array
        System.out.println("Input: " + Arrays.toString(arr7) + ", Output: " + longestDecreasingSubsequence(arr7)); // Expected: []

        int[] arr8 = {1}; // Single Element array
        System.out.println("Input: " + Arrays.toString(arr8) + ", Output: " + longestDecreasingSubsequence(arr8)); // Expected: [1]

        int[] arr9 = {3, 10, 2, 1, 20};
        System.out.println("Input: " + Arrays.toString(arr9) + ", Output: " + longestDecreasingSubsequence(arr9)); // Expected: [10, 2, 1] or [3, 2, 1]
    }

    /**
     * Time Complexity Analysis:
     * The outer loop iterates 'n' times, where 'n' is the length of the input array.
     * The inner loop iterates at most 'n' times in the worst case.
     * Thus, the overall time complexity is O(n^2).
     *
     * Space Complexity Analysis:
     * The space complexity is determined by the size of the longestSubsequence list,
     * which in the worst case can be the same size as the input array (n).
     * Therefore, the space complexity is O(n).
     */
}
