import java.util.Arrays;

public class MatrixPeakFinder {

    /**
     * Finds a peak element in a 2D matrix where rows and columns are sorted in non-decreasing order.
     * A peak element is greater than or equal to its four neighbors (top, bottom, left, right).
     *
     * @param matrix The 2D matrix to search for a peak element.
     * @return The coordinates of a peak element in the form of an integer array [row, col].
     *         Returns null if the input matrix is null or empty.
     */
    public int[] findPeak(int[][] matrix) {
        if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
            return null;
        }

        int rows = matrix.length;
        int cols = matrix[0].length;
        int low = 0;
        int high = cols - 1;

        while (low <= high) {
            int mid = low + (high - low) / 2;  // Prevent potential overflow
            int maxRowIndex = 0;

            // Find the row with the maximum value in the current column (mid)
            for (int i = 1; i < rows; i++) {
                if (matrix[i][mid] > matrix[maxRowIndex][mid]) {
                    maxRowIndex = i;
                }
            }

            // Check if the current element is a peak element
            boolean isLeftGreater = mid > 0 && matrix[maxRowIndex][mid - 1] > matrix[maxRowIndex][mid];
            boolean isRightGreater = mid < cols - 1 && matrix[maxRowIndex][mid + 1] > matrix[maxRowIndex][mid];

            if (!isLeftGreater && !isRightGreater) {
                return new int[]{maxRowIndex, mid}; // Found a peak
            } else if (isLeftGreater) {
                high = mid - 1;  // Search left half
            } else {
                low = mid + 1;  // Search right half
            }
        }

        return null; // Should not happen as a peak is guaranteed. Added for defensive programming.
    }

    /**
     * Helper method to check if a given element is a peak in the matrix.  Used for testing and verification.
     * @param matrix The matrix
     * @param row The row index of the element
     * @param col The column index of the element
     * @return true if the element at (row,col) is a peak, false otherwise.
     */
    private boolean isPeak(int[][] matrix, int row, int col) {
        int rows = matrix.length;
        int cols = matrix[0].length;
        int val = matrix[row][col];

        // Check neighbors
        if (row > 0 && matrix[row - 1][col] > val) return false; // Top
        if (row < rows - 1 && matrix[row + 1][col] > val) return false; // Bottom
        if (col > 0 && matrix[row][col - 1] > val) return false; // Left
        if (col < cols - 1 && matrix[row][col + 1] > val) return false; // Right

        return true;
    }


    public static void main(String[] args) {
        MatrixPeakFinder peakFinder = new MatrixPeakFinder();

        // Test cases
        int[][] matrix1 = {
                {1, 4, 7},
                {3, 5, 6},
                {2, 8, 9}
        };
        int[] peak1 = peakFinder.findPeak(matrix1);
        System.out.println("Matrix 1 Peak: " + Arrays.toString(peak1)); // Expected: [2, 2]

        int[][] matrix2 = {
                {1, 2},
                {3, 4}
        };
        int[] peak2 = peakFinder.findPeak(matrix2);
        System.out.println("Matrix 2 Peak: " + Arrays.toString(peak2)); // Expected: [1, 1]

        int[][] matrix3 = {
                {1, 3, 4},
                {2, 5, 6},
                {0, 7, 8}
        };
        int[] peak3 = peakFinder.findPeak(matrix3);
        System.out.println("Matrix 3 Peak: " + Arrays.toString(peak3)); // Expected: [2, 1] or [1,2]

        int[][] matrix4 = {
                {10, 8, 10, 10},
