    /**
     * Finds the defective ball among 100 identical-looking balls using a balance scale in the minimum number of steps.
     * Assumes 99 balls have the same weight, and one ball is defective (either heavier or lighter).
     *
     * @param balls An array representing the 100 balls. The index of the array represents the ball's identifier (0-99).
     * @return An array containing two elements:
     *         - The index of the defective ball (0-99).
     *         - 1 if the defective ball is heavier, -1 if lighter.
     *         Returns null if the input is invalid.
     *
     * Time Complexity: O(1) - The number of weighings is constant (at most 5).
     * Space Complexity: O(1) - Uses constant extra space.
     */
    public static int[] findDefectiveBall(int[] balls) {
        if (balls == null || balls.length != 100) {
            System.out.println("Invalid input: The input array must contain exactly 100 balls.");
            return null;
        }

        // Weighing 1: Compare balls 1-33 with balls 34-66.
        int comparisonResult1 = compare(balls, 0, 32, 33, 65);

        if (comparisonResult1 == 0) {
            // The defective ball is among balls 67-99.
            // Weighing 2: Compare balls 67-77 with normal balls (1-11)
            int comparisonResult2 = compare(balls, 66, 76, 0, 10);
            if(comparisonResult2 == 0) {
                // the defective ball is in range 78-99
                //Weighing 3: Compare balls 78-84 with balls 85-91
                int comparisonResult3 = compare(balls, 77, 83, 84, 90);
                if(comparisonResult3 == 0){
                    //ball is between 92-99
                    int[] result = findDefectiveBallLinearSearch(balls,91, 99);
                    if(result != null)
                        return result;
                    else
                        return null;
                }else{
                    //ball is between 78-91
                    int[] result = findDefectiveBallLinearSearch(balls,77, 90, comparisonResult3);
                    if(result != null)
                        return result;
                    else
                        return null;
                }


            } else {
                // The defective ball is among balls 67-77
                int[] result = findDefectiveBallLinearSearch(balls,66, 76, comparisonResult2);
                if(result != null)
                    return result;
                else
                    return null;
            }

        } else {
            // The defective ball is among balls 1-66.
            // Weighing 2: Compare balls 1-11 with balls 12-22
            int comparisonResult2 = compare(balls, 0, 10, 11, 21);
            if(comparisonResult2 == 0){
                //The defective ball is between 23-66.
                int comparisonResult3 = compare(balls, 22, 32, 33, 43);
                if(comparisonResult3 == 0){
                   //ball between 44-66
                    int[] result = findDefectiveBallLinearSearch(balls,43, 65, comparisonResult1);
                    if(result != null)
                        return result;
                    else
                        return null;
                }else{
                   //ball between 23-43
                    int[] result = findDefectiveBallLinearSearch(balls,22, 43, comparisonResult3);
                    if(result != null)
                        return result;
                    else
                        return null;
                }

            }else{
               //The defective ball is between 1-22
               int[] result = findDefectiveBallLinearSearch(balls,0, 21, comparisonResult2);
                if(result != null)
                    return result;
                else
                    return null;
            }

        }
    }

    /**
     * Helper method to compare the weight of two groups of balls.
     *
     * @param balls   The array of balls.
     * @param start1  The starting index of the first group.
     * @param end1    The ending index of the first group.
     * @param start2  The starting index of the second group.
     * @param end2    The ending index of the second group.
     * @return 1 if the first group is heavier, -1 if the second group is heavier, 0 if they are equal.
     */
    private static int compare(int[] balls, int start1, int end1, int start2, int end2) {
        int sum1 = 0;
        for (int i = start1; i <= end1; i++) {
            sum1 += balls[i];
        }
        int sum2 = 0;
        for (int i = start2; i <= end2; i++) {
            sum2 += balls[i];
        }

        if (sum1 > sum2) {
            return 1;
        } else if (sum1 < sum2) {
            return -1;
        } else {
            return 0;
        }
    }

    /**
     * Performs a linear search to find the defective ball within a given range.
     * This is used in the final stages of the algorithm when the search space is small.
     *
     * @param balls The array of balls.
     * @param start The starting index of the range.
     * @param end The ending index of the range.
     * @param comparisonResult The previously observed weight difference (+1 for heavier, -1 for lighter).  If null, checks both heavier and lighter options.
     * @return An array containing the index of the defective ball and its weight difference (+1 for heavier, -1 for lighter).  Returns null if no defective ball is found within the range.
     */
    private static int[] findDefectiveBallLinearSearch(int[] balls, int start, int end, Integer comparisonResult) {
        for (int i = start; i <= end; i++) {
            // Temporarily simulate the ball being heavier
            balls[i]++;
            int tempComparison = compare(balls, start, i, 0, 0);  //Compare the weight to some 'normal' ball
            balls[i]--; // Reset

            if (comparisonResult == null){
                if (tempComparison != 0) {
                    return new int[]{i, tempComparison};
                }
            } else if(tempComparison == comparisonResult){
                return new int[]{i, comparisonResult};
            }

            // Temporarily simulate the ball being lighter (only if heavier check failed and no comparison result given)
            if(comparisonResult == null){
                balls[i]--;
                tempComparison = compare(balls, start, i, 0, 0);
                balls[i]++;

                 if (tempComparison != 0) {
                    return new int[]{i, tempComparison};
                }
            }

        }
        System.out.println("Defective ball not found in specified range.");
        return null;
    }

     private static int[] findDefectiveBallLinearSearch(int[] balls, int start, int end) {
         return findDefectiveBallLinearSearch(balls, start, end, null);

     }

